import React, { useState, useEffect, useRef } from 'react';
import StepProgressBar from 'react-step-progress';
import 'react-step-progress/dist/index.css';
import '../index.css';
import '../App.css';
import Step1 from './step1';
import Step2 from './step2';
import Step3 from './step3';
import Step4 from './step4';
import {useDispatch, useSelector} from "react-redux";
import { formActions} from "../store/form-data-slice";
import validator from 'validator';



function App() {
  const onSubmit = () => {
    alert('submit data');
  };
  const dispatch = useDispatch();
  const formData = useSelector((state) =>state.formData.formData);
  const curFormData = useRef(formData);
  curFormData.current = formData;

  const nextOperations = useSelector((state) =>state.formData.operations.updateStep);
  const curOperations = useRef(nextOperations);
  curOperations.current = nextOperations;

  const [nextStep, setNextStep] = useState("Failed");
  const curNextStep = useRef(nextStep);
  curNextStep.current = nextStep;



  useEffect(() => {

    // If the operations has change that means the user passed the validator
    if(nextOperations.status === "Success"){
      console.log("Operations next change to Success")
      setNextStep("Success");
    }else if ( nextOperations.status === "Pending"){
      console.log("Operations next change to Pending")
      setNextStep("Pending");
    }else if (nextOperations.status === "Failed"){
      console.log("Operations next change to Failed")
      setNextStep("Failed");
    }

// Whenever dispatch called, and variables nextStep and status of iperations has change the component re-render
}, [ nextStep,nextOperations, dispatch])


const validate1 = () => {

  if(curNextStep.current === "Success"){
    // Accross all over the world,  the for a phone number length is min 4 and max 15 (without country code)
    if(curFormData.current.phoneNumber.length >= 7 && curFormData.current.phoneNumber.length <= 15 )
    return true;
    // Accross all over the world, the maximum length for a phone number is 12 (without country code)
    if(curFormData.current.phoneNumber.length < 7 || curFormData.current.phoneNumber.length > 15){
      dispatch(formActions.setOperations({
        function: 'updateStep',
        status: "Failed",
        error: "A phone number can not be less than 7 and more than 15 numbers"
    }));
    }

  }else{
    if(curNextStep.current === "Failed" && curOperations.current.error === ""){
    dispatch(formActions.setOperations({
      function: 'updateStep',
      status: "Failed",
      error: "Please fill in the fields"
  }));
}else if(curNextStep.current === "Failed" && curOperations.current.error !== ""){
  dispatch(formActions.setOperations({
    function: 'updateStep',
    status: "Success",
    error: ""
}));
}
  }
}


  return (
    <div className="app">
      <StepProgressBar
        startingStep={0}
        wrapperClass="progress-wrapper-custom"
        onSubmit={onSubmit}
        submitBtnName="Submit"
        previousBtnName="Back"
        nextBtnName="Next"
        steps={[
          {
            label: 'Step 1',
            name: 'step 1',
            content: <Step1/>,
            validator: validate1,
          },
          {
            label: 'Step 2',
            name: 'step 2',
            content: <Step2/>,
            validator: () => curNextStep.current,
          },
          {
            label: 'Step 3',
            name: 'step 3',
            content: <Step3/>,
            validator: () => curNextStep.current,
          },
          {
            label: 'Step 4',
            name: 'step 4',
            content: <Step4/>
          }
        ]}
      />
    </div>
  );
}

export default App;
