import React, { useState, useEffect } from 'react';
import StepProgressBar from 'react-step-progress';
import 'react-step-progress/dist/index.css';
import '../index.css';
import '../App.css';
import Step1 from './step1';
import Step2 from './step2';
import Step3 from './step3';
import Step4 from './step4';
import {useDispatch, useSelector} from "react-redux";
import {updateForm, formActions} from "../store/form-data-slice";


function step2Validator() {
  return true;
}

function step3Validator() {
  return true;
}

function App() {
  const onSubmit = () => {
    alert('submit data');
  };
  const dispatch = useDispatch();
  const nextOperations = useSelector((state) =>state.formData.operations.updateStep);
  const [nextStep, setNextStep] = useState("false");

  // const validation1 = () => {
  //   console.log("inside validation 1 ");
  //   i
  //   return false;
  // }

  useEffect(() => {
    // At the beggining of the component set operations with status " " (default)
    if(nextOperations.status === 'true'){
      console.log("YEEEEEES GOT THE CHANGE FROM CHILD ")
      setNextStep("true");
    }
// Whenever in this component dispatch called, set again the operations as " " (default)
}, [nextOperations.status, dispatch])





  return (
    <div class="app">
      <StepProgressBar
        startingStep={0}
        wrapperClass="progress-wrapper-custom"
        onSubmit={onSubmit}
        submitBtnName="Submit"
        previousBtnName="Back"
        nextBtnName="Next"
        steps={[
          {
            label: 'Step 1',
            name: 'step 1',
            content: <Step1/>,
            validator: () => nextStep === 'true' ? 'true':'false',
          },
          {
            label: 'Step 2',
            name: 'step 2',
            content: <Step2/>,
            validator: step2Validator
          },
          {
            label: 'Step 3',
            name: 'step 3',
            content: <Step3/>,
            validator: step3Validator
          },
          {
            label: 'Step 4',
            name: 'step 4',
            content: <Step4/>
          }
        ]}
      />
    </div>
  );
}

export default App;
