import { createSlice } from '@reduxjs/toolkit';

//Form initial State 
const initialFormState = { 
    formData:{
        name: '',
        phoneNumber: '',
        email: '',
        dateBirth: '',

    },
    operations: {
        update: {
            status: '',
            error:''
        },
        updateStep:{
            status: false,
        }

    }
}

//Form slice initialization 
const formSlice = createSlice({
    name: 'signupForm',
    initialState: initialFormState,
    reducers: {
        setFormData(state,action){
            console.log("Inside reducer");
            console.log(...action.payload);
            state.formData.name = action.payload;
        },
        setOperations(state,action){
            state.operations[action.payload.function] = {
                status : action.payload.status || '',
                error : action.payload.error || undefined
            };
        },
    }
});
export const formActions = formSlice.actions;



export const updateForm = (updateFormData) =>{
    console.log(updateFormData + " <--- from thunk");
    return (dispatch) => {
        // Setting the operations as pending
        dispatch(formActions.setOperations({status:'Pending',function:'update'}))

      // If you don't find any error above you can set operations as Success and set the data.
      dispatch(formActions.setOperations({
        function: 'update',
        status: 'Success'
    }));
    dispatch(formActions.setFormData(updateFormData));
    }
}


export default formSlice.reducer;
