import React from 'react';
import { Button } from '@material-ui/core';
import { Typography } from '@material-ui/core';
import ErrorOutlineOutlinedIcon from '@material-ui/icons/ErrorOutlineOutlined';
import { Grid } from '@material-ui/core';
import { Paper } from '@material-ui/core';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';


const Step1 = () => {

    return(
        <Paper elevation={8} >
        <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
        >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
            {language === 'el' ? 'Είσοδος' : 'Sign in'}
            </Typography>
        </Grid>

        <form className={classes.form} onSubmit={loginHandler}>
            <TextField className="textField_padding" label={language === 'el' ? 'Διεύθυνση ηλεκτρονικού ταχυδρομείου' : 'Email'} type="text" name="email" value={email} onChange={emailChangeHandler}></TextField><br />
            <TextField className="textField_padding" label={language === 'el' ? 'Κωδικός' : 'Password'} type="password" name="password" value={password} onChange={passwordChangeHandler}></TextField><br />

            <Button variant="contained" type="submit" disabled={loginOperation.status === 'Pending'}>{language === 'el' ? 'Είσοδος' : 'Sign in'}</Button>
        </form>


          <Grid item xs justifyContent="center" alignItems="center" direction="column" container >
            <Link to="/createResetPassword" variant="body2">
            {language === 'el' ? 'Ξεχάσατε τον κωδικό?' : 'Forgot Password?'}
            </Link>
          </Grid>
          <Grid item></Grid>
        {loginOperation.error &&
            <Paper elevation={3} className={classes.errorCard}>
                <Grid
                    container
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                >

                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            <ErrorOutlineOutlinedIcon />
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            {loginOperation.error}
                        </Typography>
                    </Grid>

                </Grid>
            </Paper>
        }
    </Paper>
    );
}

export default Step1;
